<span class="line"><span style="--shiki-light:#E45649;--shiki-dark:#E06C75">  DEVICE</span><span style="--shiki-light:#383A42;--shiki-dark:#ABB2BF">=</span><span style="--shiki-light:#E45649;--shiki-dark:#E06C75">ens00</span><span style="--shiki-light:#383A42;--shiki-dark:#ABB2BF">                //网卡名</span></span>
<span class="line"><span style="--shiki-light:#E45649;--shiki-dark:#E06C75">  IPADDR</span><span style="--shiki-light:#383A42;--shiki-dark:#ABB2BF">=</span><span style="--shiki-light:#986801;--shiki-dark:#D19A66">192.168.1.100</span><span style="--shiki-light:#383A42;--shiki-dark:#ABB2BF">        //设置的IP</span></span>
<span class="line"><span style="--shiki-light:#E45649;--shiki-dark:#E06C75">  NETMASK</span><span style="--shiki-light:#383A42;--shiki-dark:#ABB2BF">=</span><span style="--shiki-light:#986801;--shiki-dark:#D19A66">255.255.255.0</span><span style="--shiki-light:#383A42;--shiki-dark:#ABB2BF">       //子网掩码</span></span>
<span class="line"><span style="--shiki-light:#E45649;--shiki-dark:#E06C75">  GATEWAY</span><span style="--shiki-light:#383A42;--shiki-dark:#ABB2BF">=</span><span style="--shiki-light:#986801;--shiki-dark:#D19A66">192.168.1.1</span><span style="--shiki-light:#383A42;--shiki-dark:#ABB2BF">         //网关</span></span>
<span class="line"><span style="--shiki-light:#E45649;--shiki-dark:#E06C75">  DNS1</span><span style="--shiki-light:#383A42;--shiki-dark:#ABB2BF">=</span><span style="--shiki-light:#986801;--shiki-dark:#D19A66">192.168.1.1</span><span style="--shiki-light:#383A42;--shiki-dark:#ABB2BF">            //</span><span style="--shiki-light:#E45649;--shiki-dark:#E06C75">DNS</span></span>
<span class="line"><span style="--shiki-light:#E45649;--shiki-dark:#E06C75">  DNS2</span><span style="--shiki-light:#383A42;--shiki-dark:#ABB2BF">=</span><span style="--shiki-light:#986801;--shiki-dark:#D19A66">8.8.8.8</span><span style="--shiki-light:#383A42;--shiki-dark:#ABB2BF">                //备用DNS</span></span>
<span class="line"><span style="--shiki-light:#E45649;--shiki-dark:#E06C75">  ONBOOT</span><span style="--shiki-light:#383A42;--shiki-dark:#ABB2BF">=</span><span style="--shiki-light:#E45649;--shiki-dark:#E06C75">yes</span><span style="--shiki-light:#383A42;--shiki-dark:#ABB2BF">                  //系统启动时启动此设置</span></span>
